import React from 'react';   
import { IconButton, Button, CardMedia } from '@material-ui/core';   
import { CardHeader, Avatar, CardContent, CardActions }from '@material-ui/core'; 
import FavoriteIcon from '@material-ui/icons/Favorite';

 
const dummy={
    isLoggedIn: true,
    imagePaths:[],
    mainFeeds:[{
        User:{
            id:1,
            nickname:'정하',
        },
        content:'주말인데 비가오네요. 파전이 생각납니다.',
        //img:'http://localhost:3000/img/zeon.jpg'
        img:'https://storage.wcuisine.net/image/production/service/image/recipe/676/2b9ff3dd-3370-4103-8d0c-893ceeab1caf.jpg?size=1024x',
        
    }]
}


const PostCard = ({post}) =>{
    return(
        <>
        <hr/>
        <CardHeader
            avatar={<Avatar aria-label={post.User.nickname[0]}></Avatar>}
            action={ <Button variant="contained" color="primary" disableElevation
            style={{marginTop:0}}>
            팔로잉</Button>}
            title={post.User.nickname}
            // subheader={c.content}
        />
        <CardContent>
            {post.content}
        </CardContent>
        {/* <CardMedia 
        // className={classes.media}
        image={ post.img && post.img} 
        /> */}
        <img src={post.img} />
        <CardActions disableSpacing>
            <IconButton aria-label="add to favorites">
            <FavoriteIcon />
            </IconButton>
        </CardActions>
        </>
    )
}

export default PostCard;
