const dummyUser = {
    nickname:'지나가던사람',
    Post:[],
    Followings:['탄이','닐라','초코'],
    Followers:['안보현','박서준','임재범'], 
}


export const initialState ={
    isLoggedIn: false,
    user:{}
    
    // imagePaths:[],
};

export const LOG_IN = 'LOG_IN';
export const LOG_OUT = 'LOG_OUT';

export const loginAction={
    type: LOG_IN,  
};
export const logOutAction={
    type: LOG_OUT,  
};

const reducer = ( state = initialState, action )=>{
    switch(action.type){
        case LOG_IN:{
            return{
                ...state,
                isLoggedIn:true,
                user: dummyUser,
            }
        }
        case LOG_OUT:{
            return{
                ...state,
                isLoggedIn: false,
                user:null,
            }
        }
        default:{
            return{
                ...state,
            }
        } 
    }
}

export default reducer;
