import React from 'react'; 
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import Button from '@material-ui/core/Button';
import CardMedia from '@material-ui/core/CardMedia'; 
 
const dummy={
    isLoggedIn: true,
    imagePaths:[],
    mainFeeds:[{
        User:{
            id:1,
            nickname:'정하',
        },
        content:'주말인데 비가오네요. 파전이 생각납니다.',
        //img:'http://localhost:3000/img/zeon.jpg'
        img:'https://storage.wcuisine.net/image/production/service/image/recipe/676/2b9ff3dd-3370-4103-8d0c-893ceeab1caf.jpg?size=1024x',
       

    }]
}


const PostForm = () =>{
    return(
        <form encType="multipart/form-data">
            <TextareaAutosize aria-label="" centered
            rowsMin={9} style={{width:600, padding:15}} placeholder="오늘 하루는 어땠나요?" />      
            <input type="file" multiple hidden />
            <IconButton color="primary" aria-label="upload picture" component="span">
                <PhotoCamera />
            </IconButton> 
            <Button color="primary" style={{float:'right'}}>
            올리기</Button>
            {dummy.imagePaths.map( (v,i)=>{
                return(
                <><CardMedia 
                image={"http://localhost:3000/"+v} alt={v}
                /><Button>제거</Button>
                </>
                )
            })} 
        </form>
    )
}

export default PostForm;
