import React from 'react'; 
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import Button from '@material-ui/core/Button';
import CardMedia from '@material-ui/core/CardMedia'; 
import {useSelector} from 'react-redux';

const PostForm = () =>{
    const {imagePaths} = useSelector(state=>state.user);
    return(
        <form encType="multipart/form-data">
            <TextareaAutosize aria-label="" centered
            rowsMin={9} style={{width:600, padding:15}} placeholder="오늘 하루는 어땠나요?" />      
            <input type="file" multiple hidden />
            <IconButton color="primary" aria-label="upload picture" component="span">
                <PhotoCamera />
            </IconButton> 
            <Button color="primary" style={{float:'right'}}>
            올리기</Button>
            {imagePaths && imagePaths.map( (v,i)=>{
                return(
                <><CardMedia 
                image={"http://localhost:3000/"+v} alt={v}
                /><Button>제거</Button>
                </>
                )
            })} 
        </form>
    )
}

export default PostForm;
