import React from 'react'; 
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
import IconButton from '@material-ui/core/IconButton';
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import Button from '@material-ui/core/Button';
import CardMedia from '@material-ui/core/CardMedia'; 

import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import MoreVertIcon from '@material-ui/icons/MoreVert';  
import { makeStyles } from '@material-ui/core/styles';
import FavoriteIcon from '@material-ui/icons/Favorite';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';

import PostForm from '../components/PostForm'

const useStyles = makeStyles((theme) => ({ 
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    }
}));
const dummy={
    isLoggedIn: true,
    imagePaths:[],
    mainFeeds:[{
        User:{
            id:1,
            nickname:'정하',
        },
        content:'주말인데 비가오네요. 파전이 생각납니다.',
        //img:'http://localhost:3000/img/zeon.jpg'
        img:'https://storage.wcuisine.net/image/production/service/image/recipe/676/2b9ff3dd-3370-4103-8d0c-893ceeab1caf.jpg?size=1024x',
       

    }]
}
const Home = () =>{
    const classes = useStyles();
    return(
        <> 
            {dummy.isLoggedIn && <PostForm />
            }

            {dummy.mainFeeds.map( (c)=>{
                return(
                    <>
                    <hr/>
                    <CardHeader
                        avatar={<Avatar aria-label={c.User.nickname[0]}></Avatar>}
                        action={ <Button variant="contained" color="primary" disableElevation
                        style={{marginTop:0}}>
                        팔로잉</Button>}
                        title={c.User.nickname}
                        // subheader={c.content}
                    />
                    <CardContent>
                        {c.content}
                    </CardContent>
                    <CardMedia 
                    className={classes.media}
                    image={c.img} 
                    />

                    <CardActions disableSpacing>
                        <IconButton aria-label="add to favorites">
                        <FavoriteIcon />
                        </IconButton>
                    </CardActions>
                    </>
                );
            })}
        </>
    )
}

export default Home;
