import { all, fork, takeLatest, call, put, take, delay, takeEvery } from 'redux-saga/effects';
import {LOG_IN_SUCCESS, LOG_IN_FAILURE, SING_UP_REQUEST, LOG_IN_REQUEST, SING_UP_SUCCESS, SING_UP_FAILURE, LOG_OUT_REQUEST,LOG_OUT_SUCCESS } from '../reducers/user';
import axios from 'axios';
 
function loginAPI(){ 
    return axios.post('/login');
}

function* login(){
    try{ 
       // yield call(loginAPI);
        yield delay(100);
        yield put({
            type: LOG_IN_SUCCESS
        })
    }catch(e){
        console.error(e);
        yield put({
            type:LOG_IN_FAILURE
        })
    }
} 
function signUpAPI(){
    return axios.post('/signUp');
}
function* signUp(){
    try{ 
        //yield call(signUpAPI);
        yield delay(100);
        //throw new Error('에러');
        yield put({
            type: SING_UP_SUCCESS,
        });
    }catch(e){
        console.error(e);
        yield put({
            type:SING_UP_FAILURE,
            error:e
        })
    }
}

function* logout(){
    try{  
        yield delay(100); 
        yield put({
            type: LOG_OUT_SUCCESS,
        });
    }catch(e){
        console.error(e); 
    }
} 

function* watchSignUp(){
    yield takeEvery( SING_UP_REQUEST, signUp)
}
function* watchLogin(){
    yield takeEvery( LOG_IN_REQUEST, login)
}

function* watchLogout(){
    yield takeEvery( LOG_OUT_REQUEST, logout)
}

export default function* userSaga(){
    yield all([
        fork( watchSignUp ),
        fork( watchLogin ), 
        fork( watchLogout),
    ])
}
