import React from 'react';   
import { IconButton, Button, CardMedia } from '@material-ui/core';   
import { CardHeader, Avatar, CardContent, CardActions }from '@material-ui/core'; 
import FavoriteIcon from '@material-ui/icons/Favorite';

import { makeStyles } from '@material-ui/core/styles';
  
const useStyles = makeStyles((theme) => ({ 
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    }
}));

const PostCard = ({post}) =>{
    const classes = useStyles();
    return(
        <>
        <hr/>
        <CardHeader
            avatar={<Avatar aria-label={post.User.nickname[0]}></Avatar>}
            action={ <Button variant="contained" color="primary" disableElevation
            style={{marginTop:0}}>
            팔로잉</Button>}
            title={post.User.nickname}
            // subheader={c.content}
        />
        <CardContent>
            {post.content}
        </CardContent>
        {<CardMedia 
        className={classes.media}
        image={ post.img && post.img} />}
       
        <CardActions disableSpacing>
            <IconButton aria-label="add to favorites">
            <FavoriteIcon />
            </IconButton>
        </CardActions>
        </>
    )
}

export default PostCard;
