import React, { useEffect } from 'react';  
import { makeStyles } from '@material-ui/core/styles'; 

import PostCard from '../components/PostCard';
import PostForm from '../components/PostForm';
import { useDispatch } from 'react-redux';
import { LOG_IN } from '../reducers/user';

const useStyles = makeStyles((theme) => ({ 
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    }
}));
const dummy={
    isLoggedIn: true,
    imagePaths:[],
    mainPosts:[{
        User:{
            id:1,
            nickname:'정하',
        },
        content:'주말인데 비가오네요. 파전이 생각납니다.',
        //img:'http://localhost:3000/img/zeon.jpg'
        img:'https://storage.wcuisine.net/image/production/service/image/recipe/676/2b9ff3dd-3370-4103-8d0c-893ceeab1caf.jpg?size=1024x',
        createdAt: '2020-08-08'
    }]
}
const Home = () =>{
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch({
            type:LOG_IN,
            data:{
                nickname:'정하',
            }
        })
    },[])

    const classes = useStyles();
    return(
        <> 
            {dummy.isLoggedIn && 
                <PostForm />
            } 
            {dummy.mainPosts.map( (c)=>{
                return(
                   <PostCard post={c} key={c} />
                );
            })}
        </>
    )
}

export default Home;
