import React  from 'react';   
import Link from 'next/link';
import PropTypes from 'prop-types';
//import { Menu,  Input, Row, Col, Card, Avatar} from 'antd';  
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu'; 
 
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';


import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';

import Container from '@material-ui/core/Container';
 
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/Inbox';
import DraftsIcon from '@material-ui/icons/Drafts';


export function IconLabelTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Paper square className={classes.root}>
      <Tabs
        value={value}
        onChange={handleChange}
        variant="fullWidth"
        indicatorColor="secondary"
        textColor="secondary"
        aria-label="icon label tabs example"
      >
        <Tab icon={<PhoneIcon />} label="RECENTS" />
        <Tab icon={<FavoriteIcon />} label="FAVORITES" />
        <Tab icon={<PersonPinIcon />} label="NEARBY" />
      </Tabs>
    </Paper>
  );
}
const dummy = {
    nickname:'지나가던사람',
    Post:[],
    Followings:[],
    Followers:[],
    isLoggedIn: true,
}
const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,  
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
    }
  }));
const AppLayout = ({ children }) =>{
    const classes = useStyles();
 
  
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };
    
    return (
        <>  
            <AppBar position="static">
                <Toolbar variant="dense">
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton> 
                    <Typography variant="h6" color="inherit">
                        <Link href="/"><a>DigitalGallery</a></Link>
                    </Typography> 
                </Toolbar>
            </AppBar>      
                  
            <Paper className={classes.root} style={{marginBottom:20}}>
                {dummy.isLoggedIn ? 
                <Tabs value={value} onChange={handleChange}
                    indicatorColor="primary" textColor="primary" centered
                >
                    <Tab label={<Link href="/"><a>Home</a></Link>} />
                    <Tab label={<Link href="/myProfile"><a>myProfile</a></Link>} /> 
                </Tabs>
                :   
                <Tabs value={value} onChange={handleChange}
                    indicatorColor="primary" textColor="primary" centered
                > 
                    <Tab label={<Link href="/"><a>Home</a></Link>} />
                    <Tab label={<Link href="/login"><a>login</a></Link>} />
                    <Tab label={<Link href="/signUp"><a>sign up</a></Link>} />
                </Tabs>
                }
            </Paper> 

            <Container maxWidth="md">
            <Grid container spacing={3}  >
                {dummy.isLoggedIn && 
                <Grid item xs={3}>
                    <Card className={classes.root}>
                        <CardHeader
                            avatar={
                            <Avatar aria-label="recipe" className={classes.avatar}>
                                {dummy.nickname[0]}
                            </Avatar>
                            } 
                            title={dummy.nickname} 
                        />
                        {/* <CardActions>
                                <div key="feed">게시글{dummy.Post.length}</div>
                                <div key="following">팔로잉{dummy.Followings.length}</div>
                                <div key="follower">팔로워{dummy.Followers.length}</div>
                        </CardActions>   */}
      <List component="nav" aria-label="main mailbox folders">
        <ListItem button>
          <ListItemIcon>
            <InboxIcon />
          </ListItemIcon>
          <ListItemText primary="Inbox" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <DraftsIcon />
          </ListItemIcon>
          <ListItemText primary="Drafts" />
        </ListItem>
      </List>



                    </Card>
                </Grid>   }
                <Grid item xs= {dummy.isLoggedIn ? 9 : 12}>
                    <Card className={classes.root}> 
                    {children}  
                    </Card>
                </Grid>
            </Grid>
            </Container>
        


 
               
        </>
    )
}

// AppLayout.prototype = {
//     children: PropTypes.node,
// }

export default AppLayout
