import React from 'react';   
import { IconButton, Button, CardMedia, Card } from '@material-ui/core';   
import { CardHeader, Avatar, CardContent, CardActions }from '@material-ui/core'; 
import FavoriteIcon from '@material-ui/icons/Favorite';

import { makeStyles } from '@material-ui/core/styles';
import { checkPropTypes } from 'prop-types';

import PropTypes from 'prop-types';
  
const useStyles = makeStyles((theme) => ({ 
    media: {
      height: 0,
      paddingTop: '56.25%', // 16:9
    }
}));

const PostCard = ({post}) =>{
    const classes = useStyles();
    return(
        <>
        <hr/>
        <div key={+post.createdAt}>
            <CardHeader
                avatar={<Avatar aria-label={post.User.nickname[0]}></Avatar>}
                action={ <Button variant="contained" color="primary" disableElevation
                style={{marginTop:0}}>
                팔로잉</Button>}
                title={post.User.nickname}
                subheader={post.createdAt}
            />
            <CardContent>
                {post.content}
            </CardContent>
            { post.img && <CardMedia 
            className={classes.media}
            image={ post.img} />}
        
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                <FavoriteIcon />
                </IconButton>
            </CardActions>
        </div>
        </>
    )
}

PostCard.postTypes = {
    post: PropTypes.shape({
        User: PropTypes.object,
        content: PropTypes.string,
        img: PropTypes.string,
        createdAt: PropTypes.object,
    })
}

export default PostCard;
