import React ,{ useState ,useCallback } from 'react';  
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright Â© '}
      <Link color="inherit" href="https://material-ui.com/">
        ljh
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
  const classes = useStyles();

  const useInput = (initValue = null) =>{
    const[ value, setter] = useState(initValue);
    const handler = useCallback((e) =>{
        setter(e.target.value);
    },[]);
    return [value, handler];
  } 

  const [id, onChageId] = useInput('');
  const [nickName, onChageNickName] = useInput('');
  const [password, onChagePassword] = useInput('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [gender, onChageGender] = useInput('male');
  const [birth, onChageBirth] = useInput(''); 
  const[term, setTerm] = useState(false); 

  const[passwordError, setPasswordError ] = useState(false);
  const[termError, setTermError ] = useState(false);

  const onSubmit = useCallback((e)=>{
      e.preventDefault();

      if( password !== passwordConfirm ){
          return setPasswordError(true);
      }
      if(!term){
          return setTermError(true);
      }
      console.log({ id, nickName, password, passwordConfirm, gender, birth, term})

  },[password, passwordConfirm, term]);

  const onChagePasswordConfirm = useCallback((e)=>{
      setPasswordError(e.target.value !== password )
      setPasswordConfirm(e.target.value);
  },[password]); 
  const onChageTerm = useCallback((e)=>{
      setTermError(false);
      setTerm(e.target.checked ); 
  },[]); 


  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Sign up
        </Typography>
        <form onSubmit={onSubmit} className={classes.form} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField 
                name="user-id"
                variant="outlined"
                required
                fullWidth
                id="user-id"
                label="id"
                autoFocus
                value={id} onChange={onChageId}
              />
            </Grid>
              
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="Password"
                type="password"
                id="password" 
                value={password} onChange={onChagePassword}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                variant="outlined"
                required
                fullWidth
                name="password"
                label="passwordConfirm"
                type="password"
                id="password" 
                alue={passwordConfirm} onChange={onChagePasswordConfirm} 
              />
              {passwordError && <div className="agree">ë¹ë°ë²í¸ê° ì¼ì¹íì§ ììµëë¤</div>  }
            </Grid>
            <Grid item xs={12}>
              <FormControlLabel
                control={<Checkbox value="allowExtraEmails"  checked={term} onChange={onChageTerm}  color="primary" />}
                label="ê°ì¸ì ë³´ì²ë¦¬ë°©ì¹¨ì ëìí©ëë¤."
              />
              {termError && <div className="agree">ì½ê´ì ëìíìì¼ í©ëë¤</div>}
            </Grid>
          </Grid>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            className={classes.submit}
          >
            Sign Up
          </Button>
           
        </form>
      </div>
      <Box mt={5}>
        <Copyright />
      </Box>
    </Container>
  );
}
