import React  from 'react';   
import Link from 'next/link';
import PropTypes from 'prop-types';
//import { Menu,  Input, Row, Col, Card, Avatar} from 'antd';  
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu'; 
 
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';


import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Avatar from '@material-ui/core/Avatar';
import Grid from '@material-ui/core/Grid';


const dummy = {
    nickname:'지나가던사람',
    Post:[],
    Followings:[],
    Followers:[],
}
const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1, 
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
    }
  }));
const AppLayout = ({ children }) =>{
    const classes = useStyles();
 
  
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
      setValue(newValue);
    };
    
    return (
        <>  
            <AppBar position="static">
                <Toolbar variant="dense">
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                    </IconButton> 
                    <Typography variant="h6" color="inherit">
                        <Link href="/"><a>DigitalGallery</a></Link>
                    </Typography> 
                </Toolbar>
            </AppBar>      
                  
            <Paper className={classes.root}>
                <Tabs
                    value={value}
                    onChange={handleChange}
                    indicatorColor="primary"
                    textColor="primary"
                    centered
                >
                    <Tab label={<Link href="/"><a>Home</a></Link>} />
                    <Tab label={<Link href="/signUpp"><a>sign up</a></Link>} />
                    <Tab label={<Link href="/myProfile"><a>myProfile</a></Link>} />
                </Tabs>
            </Paper> 


            <Grid container spacing={3}>
                <Grid item xs={3}>
                    <Card className={classes.root}>
                        <CardHeader
                            avatar={
                            <Avatar aria-label="recipe" className={classes.avatar}>
                                {dummy.nickname[0]}
                            </Avatar>
                            } 
                            title={dummy.nickname}
                            actions={[
                                <div key="twit">{dummy.Post.length}</div>,
                                <div key="following">팔로잉{dummy.Followings.length}</div>,
                                <div key="follower">팔로워{dummy.Followers.length}</div>
                            ]}
                        />
                    </Card>
                </Grid>
                <Grid item xs={9}>
                    <Card className={classes.root}>
                    {children}  
                    </Card>
                </Grid>
            </Grid>






                    {/* <div className="  mdl-card mdl-cell--6-col mdl-cell--3-col-desktop mdl-grid">  
                            <Row>
                                <Col xs={24} md={6}>
                                    <Card
                                        actions={[
                                            <div key="twit">{dummy.Post.length}</div>,
                                            <div key="following">팔로잉{dummy.Followings.length}</div>,
                                            <div key="follower">팔로워{dummy.Followers.length}</div>
                                        ]}
                                    >
                                    <Card.Meta
                                        avatar={<Avatar>{dummy.nickname[0]}</Avatar>}
                                        title={dummy.nickname}
                                    />    
                                    </Card>
                                </Col>  
                            </Row>   
                    </div> */}
               
        </>
    )
}

// AppLayout.prototype = {
//     children: PropTypes.node,
// }

export default AppLayout
