import { all, fork, takeLatest, call, put, take, delay, takeEvery } from 'redux-saga/effects';
import {LOG_IN_SUCCESS, LOG_IN_FAILURE, SING_UP_REQUEST, LOG_IN_REQUEST, SING_UP_SUCCESS, SING_UP_FAILURE, } from '../reducers/user';
import axios from 'axios';
 
function loginAPI(){ 
    return axios.post('/login');
}

function* login(){
    try{ 
       // yield call(loginAPI);
        yield delay(2000);
        yield put({
            type: LOG_IN_SUCCESS
        })
    }catch(e){
        console.error(e);
        yield put({
            type:LOG_IN_FAILURE
        })
    }
} 
function signUpAPI(){
    return axios.post('/signUp');
}
function* signUp(){
    try{ 
        yield call(signUpAPI);
        yield put({
            type: SING_UP_SUCCESS
        })
    }catch(e){
        console.error(e);
        yield put({
            type:SING_UP_FAILURE
        })
    }
}

function* watchSignUp(){
    yield takeEvery( SING_UP_REQUEST, signUp)
}
function* watchLogin(){
    yield takeEvery( LOG_IN_REQUEST, login)
}
 

export default function* userSaga(){
    yield all([
        fork( watchSignUp ),
        fork( watchLogin ),  
    ])
}
