import React ,{ useCallback ,useState, useEffect }from 'react'; 
import { useSelector , useDispatch } from 'react-redux'; 
import { TextareaAutosize ,IconButton, Button, CardMedia, CircularProgress }from '@material-ui/core'; 
import PhotoCamera from '@material-ui/icons/PhotoCamera';
import { ADD_POST_REQUEST } from '../reducers/post';

const PostForm = () =>{
    const dispatch = useDispatch();
    const { imagePaths ,postAdded, isAddingPost } = useSelector(state=>state.user);
    const [ textArea, setTextArea ] = useState('');


    useEffect(()=>{
        setTextArea('');
    },[postAdded === true])

    const onSubmitForm = useCallback((e)=>{
        e.preventDefault();
        dispatch({
            type:ADD_POST_REQUEST,
            data:{
                textArea,
            }
        })
    },[]);
    const onChangeTextArea = useCallback((e)=>{
        setTextArea(e.target.value);
    },[]);

    return(
        <form encType="multipart/form-data" onSubmit={onSubmitForm}>
            <TextareaAutosize aria-label=""  value={textArea} onChange={onChangeTextArea }
            rowsMin={9} style={{width:600, padding:15}} placeholder="오늘 하루는 어땠나요?" />      
            <input type="file" multiple hidden />
            <IconButton color="primary" aria-label="upload picture" component="span">
                <PhotoCamera />
            </IconButton> 
            <Button color="primary" style={{float:'right'}} htmltype="submit"> 
                올리기{isAddingPost && <CircularProgress />}
                </Button>
            {imagePaths && imagePaths.map( (v,i)=>{
                return(
                <><CardMedia 
                image={"http://localhost:3000/"+v} alt={v}
                /><Button>제거</Button>
                </>
                )
            })} 
        </form>
    )
}

export default PostForm;
