import React  ,{useCallback} from 'react';    
import { Card, CardHeader, Avatar, Grid } from '@material-ui/core'; 
import { List, ListItem, ListItemIcon, ListItemText } from '@material-ui/core'; 
import FilterIcon from '@material-ui/icons/Filter';
import SupervisorAccountIcon from '@material-ui/icons/SupervisorAccount';
import HowToRegIcon from '@material-ui/icons/HowToReg'; 
import { useSelector, dispatch } from 'react-redux';
import Button from '@material-ui/core/Button';
import { logOutAction } from '../reducers/user';

const UserProfile = () => {
    const user = useSelector(state=>state.user.user);

    const onLogout = useCallback(()=>{
        dispatch(logOutAction);
    },[])

    return( 
        <Grid item xs={3}> 
            <Card >
                <CardHeader
                    avatar={
                    // <Avatar aria-label="recipe" className={classes.avatar}>
                    <Avatar aria-label="recipe" >
                        {user.nickname[0]}
                    </Avatar>
                    } 
                    title={user.nickname} 
                /> 
                <List component="nav" aria-label="main mailbox folders">
                    <ListItem button>
                        <ListItemIcon><FilterIcon /></ListItemIcon>
                        <ListItemText primary={'게시글 '+user.Post.length} />
                    </ListItem>
                    <ListItem button>
                        <ListItemIcon><HowToRegIcon /></ListItemIcon>
                        <ListItemText primary={'팔로잉 '+user.Followings.length} />
                    </ListItem>
                    <ListItem button>
                        <ListItemIcon><SupervisorAccountIcon /></ListItemIcon>
                        <ListItemText primary={'팔로워 '+user.Followers.length} />
                    </ListItem>
                    <ListItem>
                        <Button color="primary" onClick={onLogout}>로그아웃</Button>
                    </ListItem>
                </List>
            </Card>
        </Grid> 
        
    )
}
export default UserProfile;
