import React ,{ useState } from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import {Form, Input, Checkbox, Button } from 'antd';

const Signup = () =>{
    const[id, setId] = useState();
    const[nickName, setNickName] = useState();
    const[password, setPassword] = useState();
    const[passwordConfirm, setPasswordConfirm] = useState();
    const[gender, setGender] = useState('male');
    const[birth, setBirth] = useState();
    const[term, setTerm] = useState(false); 

    const[passwordError, setPasswordError ] = useState(false);
    const[termError, setTermError ] = useState(false);

    const onSubmit=(e)=>{
        e.preventDefault();

        if( password !== passwordConfirm ){
            return setPasswordError(true);
        }
        if(!term){
            return setTermError(true);
        }

        console.log({
            id,
            nickName,
            password,
            passwordConfirm,
            gender,
            birth,
            term,
        })
    };

    const onChageId=(e)=>{
        setId(e.target.value);
    };
    const onChageNickName=(e)=>{
        setNickName(e.target.value);
    };
    const onChagePassword=(e)=>{
        setPassword(e.target.value);
    };
    const onChagePasswordConfirm=(e)=>{
        setPasswordError(e.target.value !== password )
        setPasswordConfirm(e.target.value);
    };
    const onChageGender=(e)=>{  
        setGender(e.target.value); 
    };
    const onChageBirth=(e)=>{
        setBirth(e.target.value);
    };
    const onChageTerm=(e)=>{
        setTermError(false);
        setTerm(e.target.checked ); 
    }; 


    return (
        <> 
        <AppLayout> 
            <h2>회원가입</h2>
            <Form onSubmit={onSubmit} className="signup-form">
                <ul>
                    <li>
                        <h3><label htmlFor="user-id">아이디</label></h3>
                        <Input name="" required value={id} onChange={onChageId} />
                    </li>
                    <li>
                        <h3><label htmlFor="nick-name">닉네임</label></h3>
                        <Input name="" required value={nickName} onChange={onChageNickName} />
                    </li>
                    <li>
                        <h3><label htmlFor="password">비밀번호</label></h3>
                        <Input type="password" name="password" required value={password} onChange={onChagePassword} />
                    </li>
                    <li>
                        <h3><label htmlFor="password-confirm">비밀번호 확인</label> 
                        {passwordError && <div className="agree">비밀번호가 일치하지 않습니다</div>}</h3>
                        <Input type="password" name="password-confirm" required value={passwordConfirm} onChange={onChagePasswordConfirm} />
                       
                    </li>
                    <li>
                        <h3>성별</h3>
                        <label htmlFor="gender">남</label>
                        <input type="radio" value="male" checked name="gender" onChange={onChageGender} />
                        <label htmlFor="gender">여</label>
                        <input type="radio" value="female" name="gender" onChange={onChageGender} />
                    </li>
                    <li>
                        <h3><label htmlFor="birth">생년월일</label></h3>
                        <input type="date" name="birth" required value={birth} onChange={onChageBirth} />
                    </li>
                    <li>
                        <h3><label htmlFor="user-term">약관동의</label>
                        {termError && <div className="agree">약관에 동의하셔야 합니다</div>}</h3>
                        <Checkbox name="user-term" required value={term} onChange={onChageTerm} >
                            약관에 동의합니다.
                        </Checkbox>
                       
                    </li>
                    <li>
                        <Button type="primary" htmlType="submit" onClick={onSubmit}>확인</Button>
                    </li>
                </ul>
            </Form>
        </AppLayout>
        </>
    );
}

export default Signup;
