import React ,{ useState ,useCallback } from 'react';  
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container'; 
import RadioGroup from '@material-ui/core/RadioGroup'; 
import Radio from '@material-ui/core/Radio';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        ljh
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(3),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
}));

export default function SignUp() {
  const classes = useStyles();

  const useInput = (initValue = null) =>{
    const[ value, setter] = useState(initValue);
    const handler = useCallback((e) =>{
        setter(e.target.value); 
    },[]);
    return [value, handler];
  } 

  const [id, onChageId] = useInput('');
  const [nickName, onChageNickName] = useInput('');
  const [password, onChagePassword] = useInput('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  //const [gender, onChageGender] = useInput('male');
  const [birth, onChageBirth] = useInput(''); 
  
  const [gender, setGender] = useState('male');
  const[term, setTerm] = useState(false); 
  const[passwordError, setPasswordError ] = useState(false);
  const[termError, setTermError ] = useState(false);

  const onSubmit = useCallback((e)=>{
      e.preventDefault();

      if( password !== passwordConfirm ){
          return setPasswordError(true);
      }
      if(!term){
          return setTermError(true);
      }
      console.log({ id, nickName, password, passwordConfirm, gender, birth, term})

  },[password, passwordConfirm, term, gender]);

  const onChagePasswordConfirm = useCallback((e)=>{
      setPasswordError(e.target.value !== password )
      setPasswordConfirm(e.target.value);
  },[password]); 
  const onChageTerm = useCallback((e)=>{
      setTermError(false);
      setTerm(e.target.checked ); 
  },[]); 
  const onChageGender = useCallback((e)=>{
    console.log( e.target.value );
    setGender(e.target.value);
  },[])

  return (
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
          <LockOutlinedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          회원가입
        </Typography>
        <form onSubmit={onSubmit} className={classes.form} noValidate>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField name="user-id" id="user-id" label="아이디"
                value={id} onChange={onChageId}
                variant="outlined" required fullWidth autoFocus 
              />
            </Grid> 
            <Grid item xs={12}>
              <TextField name="nickName" id="nickName" label="닉네임"
                value={nickName} onChange={onChageNickName}
                variant="outlined" required fullWidth autoFocus 
              />
            </Grid> 
            <Grid item xs={12}>
              <TextField id="birth" type="date" label="생일"
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
                value={birth} onChange={onChageBirth} 
                variant="outlined" required fullWidth autoFocus 
              />
            </Grid> 
            <Grid item xs={12}>
              <TextField name="password" id="password" label="비밀번호"
                value={password} onChange={onChagePassword}
                variant="outlined" required fullWidth autoFocus 
              />
            </Grid>
            <Grid item xs={12}>
              <TextField name="passwordConfirm" id="passwordConfirm" label="비밀번호확인"
                value={passwordConfirm} onChange={onChagePasswordConfirm} 
                variant="outlined" required fullWidth autoFocus 
              /> 
              {passwordError && <div className="agree">비밀번호가 일치하지 않습니다</div>  }
            </Grid>

            <Grid item xs={12}></Grid>
              <RadioGroup aria-label="gender" name="gender" value={gender} onChange={onChageGender} >
                <FormControlLabel value="male" control={<Radio />}   label="남" /> 
                <FormControlLabel value="female" control={<Radio />} label="여" />
              </RadioGroup>
            </Grid>

            <Grid item xs={12}>
              <FormControlLabel
                control={<Checkbox value="allowExtraEmails" checked={term} onChange={onChageTerm}  color="primary" />}
                label="개인정보처리방침에 동의합니다."
              />
              {termError && <div className="agree">약관에 동의하셔야 합니다</div>}
            </Grid> 
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
            >
              가입하기
            </Button>
           
        </form>
      </div> 
    </Container>
  );
}
