import React ,{ useState } from 'react';
import Head from 'next/head';
import AppLayout from '../components/AppLayout';
import {Form, Input, Checkbox, Button } from 'antd';

const Signup = () =>{
    const[id, setId] = useState();
    const[nickName, setNickName] = useState();
    const[password, setPassword] = useState();
    const[passwordConfirm, setPasswordConfirm] = useState();
    const[gender, setGender] = useState();
    const[birth, setBirth] = useState();
    const[term, setTerm] = useState(); 

    const onSubmit=()=>{};

    const onChageId=(e)=>{
        setId(e.target.value);
    };
    const onChageNickName=(e)=>{
        setNickName(e.target.value);
    };
    const onChagePassword=(e)=>{
        setPassword(e.target.value);
    };
    const onChagePasswordConfirm=(e)=>{
        setPasswordConfirm(e.target.value);
    };
    const onChageGender=(e)=>{
        setGender(e.target.value); //남
        setGender(e.target.name); //gender-male 
    };
    const onChageBirth=(e)=>{
        setBirth(e.target.value);
    };
    const onChageTerm=(e)=>{
        setTerm(e.target.value);
    }; 


    return (
        <> 
        <AppLayout> 
            <h2>회원가입</h2>
            <Form onSubmit={onSubmit}>
                <ul>
                    <li>
                        <label htmlFor="user-id">아이디</label>
                        <Input name="" require value={id} onChange={onChageId} />
                    </li>
                    <li>
                        <label htmlFor="nick-name">닉네임</label>
                        <Input name="" require value={nickName} onChange={onChageNickName} />
                    </li>
                    <li>
                        <label htmlFor="password">비밀번호</label>
                        <Input name="password" require value={password} onChange={onChagePassword} />
                    </li>
                    <li>
                        <label htmlFor="password-confirm">비밀번호 확인</label>
                        <Input name="password-confirm" require value={passwordConfirm} onChange={onChagePasswordConfirm} />
                    </li>
                    <li>
                        <label htmlFor="gender">성별</label>
                        <label htmlFor="gender">남</label>
                        <input type="radio" value="male" name="gender" onChange={onChageGender} />
                        <label htmlFor="gender">여</label>
                        <input type="radio" value="female" name="gender" onChange={onChageGender} />
                    </li>
                    <li>
                        <label htmlFor="birth">생년월일</label>
                        <Input name="birth" require value={birth} onChange={onChageBirth} />
                    </li>
                    <li>
                        <label htmlFor="user-term">약관동의</label>
                        <Checkbox name="user-term" require value={term} onChange={onChageTerm} >
                            약관에 동의합니다.
                        </Checkbox>
                    </li>
                    <li>
                        <Button type="primary" htmlType="submit">확인</Button>
                    </li>
                </ul>
            </Form>
        </AppLayout>
        </>
    );
}

export default Signup;
